import { createSlice } from "@reduxjs/toolkit";

export const messageSlice = createSlice({
  name: "popUp",
  initialState: {
    replyMessage: false,
    forwardMessage: false,
  },
  reducers: {
    replyMessage: (state, action) => {
      state.replyMessage = action.payload;
    },
    forwardMessage: (state, action) => {
      state.forwardMessage = action.payload;
    },
  },
});

export const { replyMessage, forwardMessage } = messageSlice.actions;

export const APIUserData = (state) => state.popUP;

export default messageSlice.reducer;
