import React, { useEffect, useState } from "react";
import "./App.css";
import Header from "./components/Header/Header";
import Sidebar from "./components/Sidebar/Sidebar";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Mail from "./components/Mail/Mail";
import EmailList from "./components/EmailList/EmailList";
import SendMail from "./components/SendMail/SendMail";
import { useSelector } from "react-redux";
import { selectSendMessageIsOpen } from "./features/mailSlice";
import { selectUser } from "./features/userSlice";
import Login from "./components/Login/Login";
import { db } from "./firebase";
import InboxIDs from "./components/api/InboxList";
import axios from "axios";
function App() {
  const sendMessageIsOpen = useSelector(selectSendMessageIsOpen);
  const user = useSelector(selectUser);

  const [emails, setEmails] = useState([]);
  const [emailList, setEmailList] = useState([]);

  console.log("emails", emails);
  useEffect(() => {
    db.collection("emails")
      .orderBy("timestamp", "desc")
      .onSnapshot((snapshot) =>
        setEmails(
          snapshot.docs.map((doc) => ({
            id: doc.id,
            data: doc.data(),
          }))
        )
      );
    if (user) {
      InboxIDs(user)
        .get(`/${user.user_id}/messages`)
        .then((res) => {
          console.log("Email Data", res);
          setEmailList(res.data.messages);
          return res;
          // const CompletList = res.data.messages.map((messageID) => {
          //   const check = InboxIDs(user).get(
          //     `/${user.user_id}/messages/${messageID.id}`.then((res) =>
          //       console.log(res)
          //     )
          //   );
          // });
        })
        .then((res) => console.log("chained", res));
      // .catch((error) => {
      //   console.error(error);
      // });

      // const hello = InboxList.get(`/${user.user_id}/messages`);
      // console.log("ðŸš€ ~ file: App.js ~ line 34 ~ useEffect ~ hello", hello);
    }
  }, [user]);

  // Make sure the client is loaded and sign-in is complete before calling this method.

  return (
    <Router>
      {!user ? (
        <Login />
      ) : (
        <div className="app">
          <Header />
          <div className="app-body">
            <Sidebar emails={emails} />
            <Switch>
              <Route path="/mail">
                <Mail />
              </Route>
              <Route path="/" exact>
                <EmailList emails={emails} />
              </Route>
            </Switch>
          </div>

          {sendMessageIsOpen && <SendMail />}
        </div>
      )}
    </Router>
  );
}

export default App;
