import React, { useEffect, useState } from "react";
import "./App.css";
import Header from "./components/Header/Header";
import Sidebar from "./components/Sidebar/Sidebar";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import Mail from "./components/Mail/Mail";
import EmailList from "./components/EmailList/EmailList";
import SendMail from "./components/SendMail/SendMail";
import { useSelector } from "react-redux";
import { selectSendMessageIsOpen } from "./features/mailSlice";
import { selectUser } from "./features/userSlice";
import Login from "./components/Login/Login";
import { db } from "./firebase";
import { GoogleOAuthProvider } from "@react-oauth/google";
import jwt_decode from "jwt-decode";
function App() {
  const sendMessageIsOpen = useSelector(selectSendMessageIsOpen);
  const user = useSelector(selectUser);
  console.log("ðŸš€ ~ file: App.js ~ line 18 ~ App ~ user", user);
  const [emails, setEmails] = useState([]);

  console.log("emails", emails);
  useEffect(() => {
    db.collection("emails")
      .orderBy("timestamp", "desc")
      .onSnapshot((snapshot) =>
        setEmails(
          snapshot.docs.map((doc) => ({
            id: doc.id,
            data: doc.data(),
          }))
        )
      );
  }, []);
  function authenticate() {
    return window.gapi.auth2
      .getAuthInstance()
      .signIn({
        scope:
          "https://mail.google.com/ https://www.googleapis.com/auth/gmail.addons.current.message.action https://www.googleapis.com/auth/gmail.addons.current.message.metadata https://www.googleapis.com/auth/gmail.addons.current.message.readonly https://www.googleapis.com/auth/gmail.modify https://www.googleapis.com/auth/gmail.readonly",
        plugin_name: "Sohaib",
      })
      .then(
        function (res) {
          console.log("Sign-in successful", res);
          var userObject = jwt_decode(res.Cc.id_token);
          console.log(
            "ðŸš€ ~ file: Login.js ~ line 35 ~ Login ~ userObject",
            userObject
          );
        },
        function (err) {
          console.error("Error signing in", err);
        }
      );
  }
  function loadClient() {
    window.gapi.client.setApiKey("AIzaSyBDek4mda-MVBIF2wMG1t6BD2jaZjMStyc");
    return window.gapi.client
      .load("https://gmail.googleapis.com/$discovery/rest?version=v1")
      .then(
        function () {
          console.log("window.GAPI client loaded for API");
        },
        function (err) {
          console.error("Error loading window.GAPI client for API", err);
        }
      );
  }
  // Make sure the client is loaded and sign-in is complete before calling this method.

  function execute() {
    return window.gapi.client.gmail.users.messages
      .get({
        userId: "sohaibbutt448506@gmail.com",
        id: "1821b365ddf8052e",
        format: "full",
      })
      .then(
        function (response) {
          console.log("Response", response);
          var encoded = response.result.payload.parts[0].body.data;
          // console.log(
          //   "ðŸš€ ~ file: Header.js ~ line 58 ~ execute ~ encoded",
          //   Base64.decode(encoded)
          // );
        },
        function (err) {
          console.error("Execute error", err);
        }
      );
  }

  useEffect(() => {
    window.gapi.load("client:auth2", function () {
      window.gapi.auth2.init({
        client_id:
          "849443787004-gmmnien84cg9uje201v0spqiuu0408gj.apps.googleusercontent.com",
        plugin_name: "Sohaib",
      });
    });
    const load = async () => {
      await authenticate().then(loadClient);
      await execute();
    };
    if (user) {
      load();
    }
  }, [user]);

  return (
    <GoogleOAuthProvider clientId="849443787004-gmmnien84cg9uje201v0spqiuu0408gj.apps.googleusercontent.com">
      <Router>
        {!user ? (
          <Login />
        ) : (
          <div className="app">
            <Header />
            <div className="app-body">
              <Sidebar emails={emails} />
              <Switch>
                <Route path="/mail">
                  <Mail />
                </Route>
                <Route path="/" exact>
                  <EmailList emails={emails} />
                </Route>
              </Switch>
            </div>

            {sendMessageIsOpen && <SendMail />}
          </div>
        )}
      </Router>
    </GoogleOAuthProvider>
  );
}

export default App;
