import { Checkbox, IconButton } from "@material-ui/core";
import React, { useState, useMemo, useEffect } from "react";
import "./EmailList.css";
import ArrowDropDownIcon from "@material-ui/icons/ArrowDropDown";
import MoreVertIcon from "@material-ui/icons/MoreVert";
import RedoIcon from "@material-ui/icons/Redo";
import ChevronLeftIcon from "@material-ui/icons/ChevronLeft";
import ChevronRightIcon from "@material-ui/icons/ChevronRight";
import KeyboardHideIcon from "@material-ui/icons/KeyboardHide";
import SettingsIcon from "@material-ui/icons/Settings";
import InboxIcon from "@material-ui/icons/Inbox";
import PeopleIcon from "@material-ui/icons/People";
import LocalOfferIcon from "@material-ui/icons/LocalOffer";
import Section from "../Section/Section";
import EmailRow from "../EmailRow/EmailRow";
import { selectUser } from "../../features/userSlice";
import { APIUserData, userData, displayEmails } from "../../features/dataSlice";
import { useSelector, useDispatch } from "react-redux";
import InboxIDs from "../../components/api/InboxList";

function EmailList({ emailGathered }) {
  const [emailGatheredDestructurying, setEmailGatheredDestructurying] =
    useState([]);

  const user = useSelector(selectUser);
  const gh = useSelector(displayEmails);
  const data = useSelector(APIUserData);
  const dispatch = useDispatch();

  const nextPageToken = data.data?.nextPageToken;

  const [pretoken, setPreToken] = useState(null);
  const [newEmailListIDs, setNewEmailListIDs] = useState([]);
  const [newEmailGathered, setNewEmailGathered] = useState([]);

  useMemo(() => {
    {
      if (emailGathered) {
        console.log(
          "ðŸš€ ~ file: EmailList.js ~ line 40 ~ useMemo ~ emailGathered",
          emailGathered
        );
        emailGathered.map((item) => {
          const id = item.id;
          const message = item.snippet;
          const to2 = item.payload.headers.find((header) => {
            if (header.name === "From") {
              return header.value;
            }
          });
          const subject2 = item.payload.headers.find((header) => {
            if (header.name === "Subject") {
              return header.value;
            }
          });
          const date2 = item.payload.headers.find((header) => {
            if (header.name === "Date") {
              return header.value;
            }
          });

          const arr = {
            id,
            data: {
              to: to2.value,
              subject: subject2.value,
              description: message,
              timestamp: date2.value,
            },
          };
          if (emailGatheredDestructurying.length === 0) {
            setEmailGatheredDestructurying((pre) => [...pre, arr]);
          }
          if (emailGatheredDestructurying.length > 0) {
            const condition = emailGatheredDestructurying.find(
              (x) => x.id === arr.id
            );
            if (condition) {
              setEmailGatheredDestructurying((pre) => [...pre]);
            } else {
              setEmailGatheredDestructurying((pre) => [...pre, arr]);
            }
          }
        });
      }
    }
  }, [emailGathered]);

  const nextPage = () => {
    if (nextPageToken) {
      InboxIDs(user)
        .get(
          `/${user.user_id}/messages?labelIds=INBOX&maxResults=3&pageToken=${nextPageToken}`
        )
        .then((res) => {
          setEmailGatheredDestructurying([]);
          dispatch(userData(res.data));
          setNewEmailListIDs(res.data.messages);
        })
        .catch((error) => {
          console.error(error);
        });
    }
  };
  useEffect(() => {
    if (newEmailListIDs.length > 0) {
      newEmailListIDs.map((Emailid) =>
        InboxIDs(user)
          .get(`/${user.user_id}/messages/${Emailid.id}`)
          .then((res) => {
            const setting = res.data;
            setNewEmailGathered((newEmailGathered) => [
              ...newEmailGathered,
              setting,
            ]);
          })
          .catch((error) => {
            console.error(error);
          })
      );

      return () => console.log("my effect is destroying");
    }
  }, [newEmailListIDs]);
  useEffect(() => {
    const emailGathered = newEmailGathered;
    console.log(
      "ðŸš€ ~ file: EmailList.js ~ line 124 ~ useEffect ~ newEmailGathered",
      newEmailGathered
    );

    dispatch(displayEmails({ emailGathered }));
  }, [newEmailGathered]);

  return (
    <div className="emailList">
      <div className="emailList-settings">
        <div className="emailList-settingsLeft">
          <Checkbox />
          <IconButton>
            <ArrowDropDownIcon />
          </IconButton>
          <IconButton>
            <RedoIcon />
          </IconButton>
          <IconButton>
            <MoreVertIcon />
          </IconButton>
        </div>
        <div className="emailList-settingsRight">
          <IconButton
            onClick={() => {
              console.log("Go to Previous Page");
            }}
          >
            <ChevronLeftIcon />
          </IconButton>
          <IconButton
            onClick={() => {
              nextPage();
              console.log("Go to Next Page");
            }}
            disabled={nextPageToken ? false : true}
          >
            <ChevronRightIcon />
          </IconButton>
          <IconButton>
            <KeyboardHideIcon />
          </IconButton>
          <IconButton>
            <SettingsIcon />
          </IconButton>
        </div>
      </div>
      <div className="emailList-sections">
        <Section Icon={InboxIcon} title="Primary" color="red" selected />
        <Section Icon={PeopleIcon} title="Social" color="#1A73E8" />
        <Section Icon={LocalOfferIcon} title="Promotions" color="green" />
      </div>

      <div className="emailList-list">
        {emailGatheredDestructurying.map(
          ({ id, data: { to, subject, message, timestamp } }) => (
            <EmailRow
              id={id}
              key={id}
              title={to}
              subject={subject}
              description={message}
              time={timestamp}
            />
          )
        )}

        {/* {emails.map(({ id, data: { to, subject, message, timestamp } }) => (
          <EmailRow
            id={id}
            key={id}
            title={to}
            subject={subject}
            description={message}
            time={new Date(timestamp?.seconds * 1000).toUTCString()}
          />
        ))} */}
        {/* <EmailRow
          title="Twitch"
          subject="Hey fellow streamer!!"
          description="This is a DOPE"
          time="10pm"
        /> */}
      </div>
    </div>
  );
}

export default EmailList;
