import { Button } from "@material-ui/core";
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { login } from "../../features/userSlice";
// import { auth, provider } from "../../firebase";
import "./Login.css";
import jwt_decode from "jwt-decode";

function Login() {
  const dispatch = useDispatch();

  function authenticate() {
    return window.gapi.auth2
      .getAuthInstance()
      .signIn({
        scope:
          "https://mail.google.com/ https://www.googleapis.com/auth/gmail.addons.current.message.action https://www.googleapis.com/auth/gmail.addons.current.message.metadata https://www.googleapis.com/auth/gmail.addons.current.message.readonly https://www.googleapis.com/auth/gmail.modify https://www.googleapis.com/auth/gmail.readonly",
        plugin_name: "Sohaib",
      })
      .then(
        function (res) {
          console.log(
            "ðŸš€ ~ file: Login.js ~ line 22 ~ authenticate ~ res",
            res
          );
          var userObject = jwt_decode(res.Cc.id_token);
          console.log(
            "ðŸš€ ~ file: Login.js ~ line 25 ~ authenticate ~ userObject",
            userObject
          );
          localStorage.setItem("user", JSON.stringify(userObject));
          const { name, sub, picture, email } = userObject;
          const user = {
            _id: sub,
            _type: "user",
            userName: name,
            image: picture,
            email: email,
          };

          dispatch(
            login({
              displayName: user.userName,
              user_id: user._id,
              email: user.email,
              photoUrl: user.image,
            })
          );
        },
        function (err) {
          console.error("Error signing in", err);
        }
      );
  }
  function execute() {
    return window.gapi.client.gmail.users.messages
      .get({
        userId: "sohaibbutt448506@gmail.com",
        id: "1821b365ddf8052e",
        format: "full",
      })
      .then(
        function (response) {
          console.log("Response", response);
          var encoded = response.result.payload.parts[0].body.data;
          // console.log(
          //   "ðŸš€ ~ file: Header.js ~ line 58 ~ execute ~ encoded",
          //   Base64.decode(encoded)
          // );
        },
        function (err) {
          console.error("Execute error", err);
        }
      );
  }
  function loadClient() {
    window.gapi.client.setApiKey("AIzaSyBDek4mda-MVBIF2wMG1t6BD2jaZjMStyc");
    return window.gapi.client
      .load("https://gmail.googleapis.com/$discovery/rest?version=v1")
      .then(
        function () {
          console.log("window.GAPI client loaded for API");
        },
        function (err) {
          console.error("Error loading window.GAPI client for API", err);
        }
      );
  }
  useEffect(() => {
    window.gapi.load("client:auth2", function () {
      window.gapi.auth2.init({
        client_id:
          "849443787004-gmmnien84cg9uje201v0spqiuu0408gj.apps.googleusercontent.com",
        plugin_name: "Sohaib",
      });
    });
  }, []);

  const signIn = () => {
    const load = async () => {
      await authenticate().then(loadClient);
    };
    load();

    // auth.signInWithPopup(provider).then(({ user }) => {
    //   dispatch(
    //     login({
    //       displayName: user.displayName,
    //       email: user.email,
    //       photoUrl: user.photoURL,
    //     })
    //   );
    // });
  };
  return (
    <div className="login">
      <div className="login-container">
        <img
          src="https://static.dezeen.com/uploads/2020/10/gmail-google-logo-rebrand-workspace-design_dezeen_2364_col_0.jpg"
          alt=""
        />
        <Button variant="contained" color="primary" onClick={signIn}>
          Log In
        </Button>
      </div>
    </div>
  );
}

export default Login;
