import React, { useEffect, useMemo, useState } from "react";
import InboxIDs from "../api/InboxList";
import { selectUser } from "../../features/userSlice";
import { useSelector } from "react-redux";
import { Checkbox, IconButton } from "@material-ui/core";
import StarBorderOutlinedIcon from "@material-ui/icons/StarBorderOutlined";
import LabelImportantOutlinedIcon from "@material-ui/icons/LabelImportantOutlined";
import parseMessage from "gmail-api-parse-message";
const SentEmails = () => {
  const user = useSelector(selectUser);
  const [sentEmail, SetSentEmail] = useState([]);
  const [sentEmailFetched, setSentEmailFetched] = useState([]);

  useEffect(() => {
    if (sentEmailFetched.length > 0) {
      sentEmailFetched.map((item) => {
        var parsedMessage = parseMessage(item);
        console.log(
          "ðŸš€ ~ file: SentEmails.js ~ line 18 ~ sentEmailFetched.map ~ parsedMessage",
          parsedMessage
        );
        const condition = sentEmail.find((array) => array.id === item.id);
        if (condition) {
          return;
        } else {
          const arr = {
            id: parsedMessage.id,

            to: parsedMessage.headers.to,
            subject: parsedMessage.headers.subject,
            description: parsedMessage.snippet,
            timestamp: parsedMessage.headers.date,
          };
          return SetSentEmail((sentEmail) => [...sentEmail, parsedMessage]);
        }
      });
    }
  }, [sentEmailFetched]);

  useEffect(() => {
    InboxIDs(user)
      .get(`/${user.user_id}/messages?labelIds=SENT`)

      .then(
        function (response) {
          response.data.messages.map(async (item) => {
            await InboxIDs(user)
              .get(`/${user.user_id}/messages/${item.id}`)
              .then((res) => {
                setSentEmailFetched((sentEmailFetched) => [
                  ...sentEmailFetched,
                  res.data,
                ]);
              })
              .catch((error) => {
                console.error(error);
              });
          });
        },
        function (err) {
          console.error("Execute error", err);
        }
      );
  }, [user]);

  return (
    <div>
      <div className="emailRow">
        <div className="emailRow-options">
          <Checkbox />
          <IconButton>
            <StarBorderOutlinedIcon />
          </IconButton>
          <IconButton>
            <LabelImportantOutlinedIcon />
          </IconButton>
        </div>

        {/* <h3 className="emailRow-title">{title}</h3>
        <div className="emailRow-message">
          <h4>
            {subject}{" "}
            <span className="emailRow-description"> - {description}</span>
          </h4>
        </div>
        <p className="emailRow-time">{time}</p> */}
      </div>
    </div>
  );
};

export default SentEmails;
